<?php  if ( ! defined('BASEPATH')) exit('No direct script access allowed');if ( ! function_exists('css_url')){	function css_url($nom){		return base_url() . 'assets/intranet/css/' . $nom . '.css';	}}if ( ! function_exists('js_url')){	function js_url($nom){		return base_url() . 'assets/intranet/javascript/' . $nom . '.js';	}}if ( ! function_exists('img_url')){	function img_url($nom){		return base_url() . 'assets/intranet/images/' . $nom;	}}if ( ! function_exists('son_url')){	function son_url($nom){		return base_url() . 'assets/intranet/sons/' . $nom;	}}if ( ! function_exists('file_url')){	function file_url($nom){		return base_url() . 'assets/intranet/file/' . $nom;	}}